import { useEffect, useState } from 'react';
import { Autocomplete, TextField } from "@mui/material";
import { async } from 'q';
require('isomorphic-fetch');
var Dropbox = require('dropbox').Dropbox;


const accessToken = 'sl.BTNgtGKtgpAoU_vwO0CnqfR1_q8oYP2glvDT00rErhL_p1XFkTtcEpJ34daq-_-WzM5EWXYqWukOfidfmRofFId1VCFgAofJNSnuMd5xkeDSdIO4Jqn8fyzYvoxZCxYUHl6tAIdx00b7'
var dbx = new Dropbox({ accessToken });

const getSharableLink = async(item) => {
   const shareablelink = await dbx.sharingCreateSharedLinkWithSettings({path:item.path_lower})
   console.log(shareablelink);
}
const getSearchList = async ()=> {

  // Create an instance of Dropbox with the access token and use it to
  // fetch and render the files in the users root directory.

  //  const searchedFile = await dbx.filesSearchV2({query:"Smile"})
  //  return (searchedFile.result.matches);

   const response = await dbx.filesListFolder({path: '/amazon primevideo/movies'})

   return response.result.entries;

}


function debounce(func, wait) {
  let timeout
  return function(...args) {
    const context = this
    clearTimeout(timeout)
    timeout = setTimeout(() => func.apply(context, args), wait)
  }
}


  function App () {
    const [list, setList] = useState([]);

