    // },[]);

    return (
      <div className="App">
        <Autocomplete
          disablePortal
          id="search-movie-tv"
          options={[]}
          sx={{ width: 300 }}
          renderInput={(params) => <TextField {...params} label="Movie - Tv Series" />}
          onInputChange={async (e) => {
            if(e.target.value){
              const res =  debounce(getSearchList,e.target.value,1000)
              console.log(res);
            }

          }}
        />
      </div>
    );
  }

export default App;

