import './App.css';
import { useEffect, useState,useCallback } from 'react';
import { Autocomplete, TextField ,Alert} from "@mui/material";
import { async } from 'q';
require('isomorphic-fetch');
var Dropbox = require('dropbox').Dropbox;


const accessToken = 'sl.BTNgtGKtgpAoU_vwO0CnqfR1_q8oYP2glvDT00rErhL_p1XFkTtcEpJ34daq-_-WzM5EWXYqWukOfidfmRofFId1VCFgAofJNSnuMd5xkeDSdIO4Jqn8fyzYvoxZCxYUHl6tAIdx00b7'
var dbx = new Dropbox({ accessToken });


const getSharableLink = async(item) => {
   const shareablelink = await dbx.sharingCreateSharedLinkWithSettings({path:item.path_lower})
   console.log(shareablelink);
}



function debounce(func, wait) {
  let timeout;
  return function(...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}



  function App () {
    const [list, setList] = useState([]);
    const [error, setError] = useState('');

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const debounceOnChange = useCallback(debounce(onChange, 400), []);

    function onChange(value) {
      if(value)
       dbx.filesSearchV2({query:"Smile"}).then((resp) => {

        const responseList  =resp.result.matches.map( item => {
          return item.metadata.metadata
        } )
        console.log(responseList,'responseList');
        console.log(responseList.filter( li => li[".tag"]==="file"));
        // setList(responseList.filter( li => videoFormats.some((format) => li.path_lower.includes(format))))
        setList(responseList)
       }).catch((e) => {
          setError(e)
       })


    }
console.log('list',list);

    return (
      <div className="App">
      {error && <Alert severity="error">{error}!</Alert>}

        <Autocomplete
          disablePortal
          id="search-movie-tv"
          options={list.map( li => {            
            return   { label: li.name, year: 2008 }
          } )}
          sx={{ width: 300 }}
          renderInput={(params) => <TextField {...params} label="Movie - Tv Series" />}
          onInputChange={debounceOnChange}
        />
      </div>
    );
  }

export default App;

