 function SearchList(props) {
    const {options} = props;
  return (
    <div>
      <List>
        <ListItem>
        {options.map( op => {

          return     <ListItemButton>
          <ListItemIcon></ListItemIcon>
          <ListItemText primary="Inbox" />
        </ListItemButton>

        })}
        </ListItem>
      </List>
    </div>
  );
}


function debounce(func, wait) {
  let timeout;
  return function(...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}



  function App () {
    const [list, setList] = useState([]);
    const [error, setError] = useState('');

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const debounceOnChange = useCallback(debounce(onChange, 400), []);

    function onChange(e) {
      const value = e.target.value;
      if(value)
       dbx.filesSearchV2({query:value}).then((resp) => {
       const responseList  =resp.result.matches.map( item => {
          return item.metadata.metadata
        } )
        const onlyFiles = responseList.filter( li => li[".tag"]==="file")
        setList(onlyFiles)
       }).catch((e) => {
          setError(e.message)
       })
    }



    if(error){
      return <Alert severity="error">{error}!</Alert>
    }

    return (
      <div className="DropboxContainer">
        <div className='SearchDropBox'>
        <TextField id="search-movie-tv" label="Movie - Tv Series" variant="standard"  onChange={debounceOnChange} sx={{ width: 600 }} /></div>
        <SearchList  options={list} />
      </div>
    );
  }

export default App;
