import imdb from "imdb-api";
import "./App.css";
import React, { useState, useCallback } from "react";
import { Button, TextField, Alert, Card } from "@mui/material";

require("isomorphic-fetch");
var Dropbox = require("dropbox").Dropbox;

const apiKey = "k_bx94gdhj";

const cli = new imdb.Client({ apiKey });

const getVideo = async (item) => {
  const shareablelink = await dbx.sharingCreateSharedLinkWithSettings({
    path: item.path_lower,
  });
  console.log(shareablelink);
};

function DropboxContainer() {
  const [list, setList] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [itemId, setItemId] = useState("");

  const getResourceDetails = (e) => {
    if (itemId) {
      setLoading(true);
      imdb
        .get({ name: "The Toxic Avenger" }, { apiKey: "foo", timeout: 30000 })
        .then(console.log)
        .catch(console.log);
    }
  };

  if (error) {
    return <Alert severity="error">{error}!</Alert>;
  }
  //tt1441109

  return (
    <Card variant="outlined">
      <h2 className="Heading">IMDB</h2>
      <div className="ImdbContainer">
        <TextField
          id="search-movie-tv"
          label="Imdb ID"
          variant="standard"
          sx={{ width: 400 }}
          onChange={(e) => {
            setItemId(e.target.value);
          }}
          value={itemId}
        />
      </div>

      <Button onClick={getResourceDetails}> Set Details </Button>
    </Card>
  );
}

export default DropboxContainer;
