import "./App.css";
import React, { useState } from "react";
import { Button, TextField, Alert, Card } from "@mui/material";

require("isomorphic-fetch");
const imdb = require("imdb-api");
const apiKey = "k_tzrvo3ni";

const cli = new imdb.Client({ apiKey });

function DropboxContainer() {
  const [movieInfo, setMovieInfo] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [itemId, setItemId] = useState("");

  const getResourceDetails = () => {
    if (itemId) {
      setLoading(true);
      fetch(
        `https://imdb-api.com/en/API/Title/${apiKey}/${itemId}/FullActor,Posters`
      )
        .then((response) => response.json())
        .then((data) => {
          // console.log(data)
          setMovieInfo(data);
          setLoading(false);
        });
    }
  };

  if (error) {
    return <Alert severity="error">{error}!</Alert>;
  }
  console.log(movieInfo);

  return (
    <Card variant="outlined">
      <h2 className="Heading">IMDB</h2>
      <div className="ImdbContainer">
        <TextField
          id="search-movie-tv"
          label="Imdb ID"
          variant="standard"
          sx={{ width: 400 }}
          onChange={(e) => {
            setItemId(e.target.value);
          }}
          value={itemId}
        />
      </div>

      <Button variant="contained" onClick={getResourceDetails}>
        Set Details{" "}
      </Button>
    </Card>
  );
}

export default DropboxContainer;
