 function SearchList() {
  return (
    <Box sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
      <nav aria-label="main mailbox folders">
        <List>
          <ListItem disablePadding>
            <ListItemButton>
              <ListItemIcon>
              </ListItemIcon>
              <ListItemText primary="Inbox" />
            </ListItemButton>
          </ListItem>
          <ListItem disablePadding>
            <ListItemButton>
              <ListItemIcon>
              </ListItemIcon>
              <ListItemText primary="Drafts" />
            </ListItemButton>
          </ListItem>
        </List>
      </nav>
      <Divider />
      <nav aria-label="secondary mailbox folders">
        <List>
          <ListItem disablePadding>
            <ListItemButton>
              <ListItemText primary="Trash" />
            </ListItemButton>
          </ListItem>
          <ListItem disablePadding>
            <ListItemButton component="a" href="#simple-list">
              <ListItemText primary="Spam" />
            </ListItemButton>
          </ListItem>
        </List>
      </nav>
    </Box>
  );
}


function debounce(func, wait) {
  let timeout;
  return function(...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}



  function App () {
    const [list, setList] = useState([]);
    const [error, setError] = useState('');

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const debounceOnChange = useCallback(debounce(onChange, 400), []);

    function onChange(e) {
      const value = e.target.value;
      if(value)
       dbx.filesSearchV2({query:value}).then((resp) => {
       const responseList  =resp.result.matches.map( item => {
          return item.metadata.metadata
        } )
        const onlyFiles = responseList.filter( li => li[".tag"]==="file")
        setList(onlyFiles)
       }).catch((e) => {
          setError(e.message)
       })
    }



    if(error){
      return <Alert severity="error">{error}!</Alert>
    }

    return (
      <div className="App">
        <TextField id="search-movie-tv" label="Movie - Tv Series" variant="standard"  onChange={debounceOnChange} sx={{ width: 300 }} />
        <SearchList  options={list} />
      </div>
    );
  }

export default App;
