import { Button, Alert, Checkbox } from "@mui/material";
import React, { useState } from "react";
import DropBoxCont from "./DropBox";
import Imdb from "./Imdb";
import OTT from "./Ott";
import { getMovies, setMovie } from "./FireBase";

function App() {
  const [fsData, setFsData] = useState({});
  const [error, setError] = useState(false);
  const [isMovie, setIsMovie] = useState(false);
  const [isInCarausal, setIsInCarausal] = useState(false);
  const [ott, setOtt] = useState("Netflix");
  const dbxData = (data) => {
    setFsData({ ...fsData, ...data });
  };

  const imdbData = (data) => {
    setFsData({ ...fsData, ...data });
  };

  const addEntry = () => {
    fsData["iscarousel"] = isInCarausal;
    fsData["isTrending"] = true;
    fsData["sourceType"] = ott;
    fsData["isSeries"] = !isMovie;

    let missingKeys = [];
    Object.keys(fsData).forEach((k) => {
      if (!fsData[k]) {
        missingKeys.push(k);
      }
    });

    if (missingKeys.length) {
      console.log(missingKeys);
      setError(
        "key missing",
        missingKeys.map((k) => k)
      );
    } else {
      // setMovie(fsData);
    }
    console.log(fsData);
  };

  const label = { inputProps: { "aria-label": "Checkbox demo" } };
  return (
    <>
      <div className="MovieType">
        <span> Resource Type : {isMovie ? "Movie" : "Tv Series"}</span>
        <Button
          variant="contained"
          onClick={() => {
            setIsMovie(!isMovie);
          }}
        >
          Change
        </Button>
        is in carasousal:
        <Checkbox {...label} onClick={() => setIsInCarausal(!isInCarausal)} />
        <OTT
          onChange={(v) => {
            console.log(v.target.value);
            setOtt(v);
          }}
        />
      </div>
      <DropBoxCont setData={dbxData} />
      {error && <Alert severity="error">{error}!</Alert>}
      <Imdb setData={imdbData} />
      <Button
        variant="contained"
        onClick={addEntry}
        sx={{ marginLeft: "100px", marginTop: "50px" }}
      >
        Add Entry to DataBase
      </Button>
    </>
  );
}

export default App;

