   console.log(item.metadata.metadata.path_lower);
   return <li key={item.metadata.metadata.id}> {item.metadata.metadata.path_lower}
   <button onClick={() => getSharableLink(item.metadata.metadata)}> Get Link </button>
   </li>
 } )}
 </ul>
