import "./App.css";
import React, { useState, useCallback } from "react";
import { Button, TextField, Alert, Card } from "@mui/material";

require("isomorphic-fetch");
var Dropbox = require("dropbox").Dropbox;

const accessToken =
  "sl.BTRBWaeluM0rSLZ4O39qLcapVUj4n3AIQsJwwLNkg-7vdK5lNdH9AfIFsGLIbPpAQXdjIvto4NybFu66evIPplQVMJ-Scn1R7oLQJ4nFVN5JY4m-Bjht96p6LXl5MBbrL0DgNqEUiWEJ";
var dbx = new Dropbox({ accessToken });

const getSharableLink = async (item) => {
  const shareablelink = await dbx.sharingCreateSharedLinkWithSettings({
    path: item.path_lower,
  });
  console.log(shareablelink);
};

/**
 * 
 * @param {     // <Button variant="contained" onClick={() => getSharableLink(item)}>
 //   Get Link
 // </Button>} props 
 * @returns 
 */

function SearchList(props) {
  const { options, onItemClick } = props;
  return (
    <ul className="DropboxList">
      {options.map((item) => {
        return (
          <li
            className="DropboxListItem"
            key={item.id}
            onClick={() => {
              onItemClick(item);
            }}
          >
            {item.name}
          </li>
        );
      })}
    </ul>
  );
}

function debounce(func, wait) {
  let timeout;
  return function (...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}

function App() {
  const [list, setList] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [selectedResource, setSelectedResource] = useState(null);

  const onChange = (e) => {
    const value = e.target.value;
    if (value) {
      setLoading(true);
      dbx
        .filesSearchV2({ query: value })
        .then((resp) => {
          const responseList = resp.result.matches.map((item) => {
            return item.metadata.metadata;
          });
          console.log(responseList, "responseList");
          const onlyFiles = responseList.filter((li) => li[".tag"] === "file");
          console.log(onlyFiles.length, "only files");
          setList(onlyFiles);
          setLoading(false);
        })
        .catch((e) => {
          setError(e.message);
          setLoading(false);
        });
    }
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const debounceOnChange = useCallback(debounce(onChange, 400), []);

  const onItemClick = (item) => {
    setSelectedResource(item);
  };

  if (error) {
    return <Alert severity="error">{error}!</Alert>;
  }

  return (
    <Card variant="outlined">
      <div className="DropboxContainer">
        <div className="SearchDropBox">
          <TextField
            id="search-movie-tv"
            label="Movie - Tv Series"
            variant="standard"
            onChange={debounceOnChange}
            sx={{ width: 400 }}
          />
        </div>
        {loading ? (
          <p>loading...</p>
        ) : selectedResource ? (
          <div>
            <p> {selectedResource.name} </p> <span>&#10006</span>
          </div>
        ) : (
          <SearchList options={list} onItemClick={onItemClick} />
        )}
      </div>
    </Card>
  );
}

export default App;
