import './App.css';
import {  useState,useCallback } from 'react';
import { Button, TextField ,Alert} from "@mui/material";
require('isomorphic-fetch');
var Dropbox = require('dropbox').Dropbox;


const accessToken = 'sl.BTNgtGKtgpAoU_vwO0CnqfR1_q8oYP2glvDT00rErhL_p1XFkTtcEpJ34daq-_-WzM5EWXYqWukOfidfmRofFId1VCFgAofJNSnuMd5xkeDSdIO4Jqn8fyzYvoxZCxYUHl6tAIdx00b7'
var dbx = new Dropbox({ accessToken });


const getSharableLink = async(item) => {
   const shareablelink = await dbx.sharingCreateSharedLinkWithSettings({path:item.path_lower})
   console.log(shareablelink);
}


function ListItem(props) {
  const {options} = props;
  return <ul>
  {options.map((item) => {
    return (
      <li key={item.id}>
        {item.path_lower}
        <Button variant="contained" onClick={() => getSharableLink(item)} > Get Link</Button>
          Get Link
      </li>
    );
  })}
</ul>;
}


function debounce(func, wait) {
  let timeout;
  return function(...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}



  function App () {
    const [list, setList] = useState([]);
    const [error, setError] = useState('');

    // eslint-disable-next-line react-hooks/exhaustive-deps
    const debounceOnChange = useCallback(debounce(onChange, 400), []);

    function onChange(e) {
      const value = e.target.value;
      if(value)
       dbx.filesSearchV2({query:value}).then((resp) => {
       const responseList  =resp.result.matches.map( item => {
          return item.metadata.metadata
        } )

        const onlyFiles = responseList.filter( li => li[".tag"]==="file")
        setList(onlyFiles)
       }).catch((e) => {
          setError(e.message)
       })


    }

    const options = list.map((li) => {
      return { label: li.name, year: 2008 };
    });
    console.log(options,'opio');


    if(error){
      return <Alert severity="error">{error}!</Alert>
    }

    return (
      <div className="App">
        <TextField id="search-movie-tv" label="Movie - Tv Series" variant="standard"  onChange={debounceOnChange} sx={{ width: 300 }} />
        <ListItem  options={options} />
      </div>
    );
  }

export default App;
