import "./App.css";
import React, { useState, useCallback } from "react";
import { Button, TextField, Alert, Card } from "@mui/material";

require("isomorphic-fetch");
var Dropbox = require("dropbox").Dropbox;

const accessToken =
  "sl.BTWSfVpwd5HjWuxkwPEXcgdcqSneZ20Hi-ue73WfLE__W0nHI8HGKfqOQpFifB7BwBoxbV5sRmwxEmUsarZm1gmPg7FWO4DMEMMdCoRToB8cmMVbtFY_v6Oo8vWa3_0KdOsW3zDDjF-B";
var dbx = new Dropbox({ accessToken });

function SearchList(props) {
  const { options, onItemClick } = props;
  return (
    <ul className="DropboxList">
      {options.map((item) => {
        return (
          <li
            className="DropboxListItem"
            key={item.id}
            onClick={() => {
              onItemClick(item);
            }}
          >
            {item.name}
          </li>
        );
      })}
    </ul>
  );
}

function debounce(func, wait) {
  let timeout;
  return function (...args) {
    const context = this;
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      timeout = null;
      func.apply(context, args);
    }, wait);
  };
}

function DropboxContainer() {
  const [list, setList] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [selectedResource, setSelectedResource] = useState(null);
  const [shareablelink, setShareablelink] = useState(null);

  const getSharableLink = async (item) => {
    try {
      const dpxLink = await dbx.sharingCreateSharedLinkWithSettings({
        path: item.path_lower,
      });
      console.log(dpxLink);
      setShareablelink(dpxLink);
    } catch (error) {
      setError(error.message);
    }
  };

  const onChange = (e) => {
    const value = e.target.value;
    if (value) {
      setLoading(true);
      dbx
        .filesSearchV2({ query: value })
        .then((resp) => {
          const responseList = resp.result.matches.map((item) => {
            return item.metadata.metadata;
          });
          console.log(responseList, "responseList");
          const onlyFiles = responseList.filter((li) => li[".tag"] === "file");
          console.log(onlyFiles.length, "only files");
          setList(onlyFiles);
          setLoading(false);
        })
        .catch((e) => {
          setError(e.message);
          setLoading(false);
        });
    }
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const debounceOnChange = useCallback(debounce(onChange, 400), []);

  const onItemClick = (item) => {
    setSelectedResource(item);
  };

  if (error) {
    return;
  }

  return (
    <Card variant="outlined">
      {error && <Alert severity="error">{error}!</Alert>}
      <h2 className="Heading">DropBox</h2>
      <div className="DropboxContainer">
        <div className="SearchDropBox">
          <TextField
            id="search-movie-tv"
            label="Movie - Tv Series"
            variant="standard"
            onChange={debounceOnChange}
            sx={{ width: 400 }}
          />
        </div>
        {loading ? (
          <p>loading...</p>
        ) : selectedResource ? (
          <div>
            <p>
              {selectedResource.name}
              <span
                className="CloseSelected"
                onClick={(e) => {
                  e.stopPropagation();
                  setSelectedResource(null);
                }}
              >
                &#10006;
              </span>
            </p>
            <Button
              variant="contained"
              onClick={() => {
                getSharableLink(selectedResource);
              }}
            >
              {" "}
              Set VideoPlayer Link
            </Button>
          </div>
        ) : (
          <SearchList options={list} onItemClick={onItemClick} />
        )}
        {shareablelink ? <p> link generated: {"shareablelink"} </p> : <></>}
      </div>
    </Card>
  );
}

export default DropboxContainer;
