import "./App.css";
import React, { useState } from "react";
import { Button, TextField, Alert, Card } from "@mui/material";
const apiKey = "k_tzrvo3ni";

function DropboxContainer() {
  const [movieInfo, setMovieInfo] = useState({});
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [itemId, setItemId] = useState("");
  const [images, setImages] = useState("");

  const getResourceDetails = () => {
    if (itemId) {
      setLoading(true);
      fetch(
        `https://imdb-api.com/en/API/Title/${apiKey}/${itemId}/FullActor,Posters`
      )
        .then((response) => response.json())
        .then((data) => {
          setMovieInfo(data);
          setLoading(false);
        })
        .catch((e) => {
          setError(e.message);
        });

      fetch(`https://imdb-api.com/en/API/Images/${apiKey}/${itemId}`)
        .then((response) => response.json())
        .then((data) => {
          setImages(data);
          if (data.items) {
            const imgs = data.items
              .filter((i) => !i.title.includes("event"))
              .map((i) => i.image);
          }
          setLoading(false);
        })
        .catch((e) => {
          setError(e.message);
        });
    }

    // /k_tzrvo3ni/tt1375666
  };

  if (Object.keys(movieInfo).length) {
    const fsObject = {
      imdbId: movieInfo.id,
      name: movieInfo.title,
      description: movieInfo.plot,
      keywordList: movieInfo.keywordList,
      languageList: movieInfo.languageList.map((l) => l.value),
      runTime: movieInfo.runtimeStr,
      rating: movieInfo.imDbRating,
      stars: movieInfo.starList.map((s) => s.name),
      genre: movieInfo.genres,
      landscapeImages: [],
    };

    console.log(fsObject);
  }

  return (
    <Card variant="outlined">
      {error && <Alert severity="error">{error}!</Alert>}
      <h2 className="Heading">IMDB</h2>
      <div className="ImdbContainer">
        <TextField
          id="search-movie-tv"
          label="Imdb ID"
          variant="standard"
          sx={{ width: 400 }}
          onChange={(e) => {
            setItemId(e.target.value);
          }}
          value={itemId}
        />
      </div>

      <Button variant="contained" onClick={getResourceDetails}>
        Set Details
      </Button>

      {loading ? (
        <p> loading... </p>
      ) : (
        <div className="MovieDetails">
          <ul>
            <li> imdbId : {movieInfo["id"]} </li>
          </ul>
        </div>
      )}
    </Card>
  );
}

export default DropboxContainer;
