import { Button, Alert } from "@mui/material";
import React, { useState } from "react";
import DropBoxCont from "./DropBox";
import Imdb from "./Imdb";
import { getMovies, setMovie } from "./FireBase";

function App() {
  const [fsData, setFsData] = useState({});
  const [error, setError] = useState(false);
  const dbxData = (data) => {
    setFsData({ ...fsData, ...data });
  };

  const imdbData = (data) => {
    setFsData({ ...fsData, ...data });
  };

  const addEntry = () => {
    let missingKeys = [];
    Object.keys(fsData).forEach((k) => {
      if (!fsData[k]) {
        missingKeys.push(k);
      }
    });

    if (missingKeys.length) {
      setError(
        "key missing",
        missingKeys.map((k) => k)
      );
    } else {
      setMovie(fsData);
    }
  };

  return (
    <>
      <DropBoxCont setData={dbxData} />

      {error && <Alert severity="error">{error}!</Alert>}
      <Imdb setData={imdbData} />
      <Button
        variant="contained"
        onClick={addEntry}
        sx={{ marginLeft: "100px", marginTop: "50px" }}
      >
        Add Entry to DataBase
      </Button>
    </>
  );
}

export default App;
